(function($){$.fn.jqSlider=function(options){var defaults={auto:false,slideWidth:900};var options=$.extend(defaults,options);this.each(function(){var obj=$(this);var currentPosition=0;var slideWidth=options.slideWidth;var slides=$(".slide",obj);var numberOfSlides=slides.length;$("#slidesContainer",obj).css({"overflow":"hidden","width":slideWidth});slides.wrapAll('<div id="slideInner"></div>').css({"float":"left","width":slideWidth});$("#slideInner").css("width",slideWidth*numberOfSlides);obj.prepend('<span class="control" id="leftControl">left</span>').append('<span class="control" id="rightControl">right</span>');
manageControls(currentPosition);$(".control").bind("click",function(){currentPosition=$(this).attr("id")=="rightControl"?currentPosition+1:currentPosition-1;manageControls(currentPosition);$("#slideInner").animate({"marginLeft":slideWidth*-currentPosition})});function manageControls(position){if(position==0)$("#leftControl").hide();else $("#leftControl").show();if(position==numberOfSlides-1)$("#rightControl").hide();else $("#rightControl").show()}})}})(jQuery);