(function($){$.fn.jqSlider=function(options){var defaults={auto:false,slideWidth:900,clickPause:false};var options=$.extend(defaults,options);this.each(function(){var obj=$(this);var currentPosition=0;var slideWidth=options.slideWidth;var slides=$(".slide",obj);var nos=slides.length;$("#slidePanel",obj).css({"overflow":"hidden","width":slideWidth});slides.wrapAll('<div id="innerPanel"></div>').css({"float":"left","width":slideWidth});$("#innerPanel").css("width",slideWidth*nos);obj.prepend('<span class="control" id="leftControl">left</span>').append('<span class="control" id="rightControl">right</span>');
manageControls(currentPosition);$(".control").bind("click",function(){currentPosition=$(this).attr("id")=="rightControl"?currentPosition+1:currentPosition-1;if(options.clickPause)clearInterval(t);manageControls(currentPosition);$("#innerPanel").animate({"marginLeft":slideWidth*-currentPosition})});if(options.auto)var t=setInterval(autoslide,6E3);function autoslide(){currentPosition=currentPosition+1;if(currentPosition==nos)currentPosition=0;manageControls(currentPosition);$("#innerPanel").animate({"marginLeft":slideWidth*
-currentPosition})}function manageControls(position){if(position==0)$("#leftControl").hide();else $("#leftControl").show();if(position==nos-1)$("#rightControl").hide();else $("#rightControl").show()}})}})(jQuery);